const Typewriter = require('t-writer.js');


const target1 = document.getElementById('teste1');

const target2 = document.getElementById('teste2'); 

const target = document.getElementById('teste');

const writer = new Typewriter(target, {
  loop: true,
  typeSpeed: 350,
  typeColor: 'white',
  cursorColor: 'white'
})

writer
  .changeTypeColor('white')
  .type("BARREIRAS")
  .rest(350)
  .clear()
  .changeTypeColor('#2E7D32')
  .type("GLÚTEN")
  .rest(350)
  .clear()
  .changeTypeColor('#f9a825')
  .type("AÇÚCAR")
  .rest(350)
  .clear()
  .changeTypeColor('#C62828')
  .type("LACTOSE")
  .rest(350)
  .clear()
  .then(writer.start.bind(writer))

  .start()
     